# 登录卡片组件
<template>
  <div class="card login-card">
    <img src="/logo.svg" alt="Logo" class="logo">
    <h2>Welcome Back!</h2>
    <p>To keep connected with us please login with your personal info</p>
    <button class="switch-btn" @click="$emit('switch-view')">SIGN IN</button>
  </div>
</template>

<script setup>
defineEmits(['switch-view'])
</script>

<style scoped>
.card {
  position: absolute;
  top: 0;
  height: 100%;
  transition: all 0.6s ease-in-out;
  padding: 50px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
}

.login-card {
  left: 0;
  width: 50%;
  background: #41B883;
  color: white;
  transform: translateX(0);
}

.auth-container.show-signup .login-card {
  transform: translateX(100%);
}

.logo {
  width: 80px;
  margin-bottom: 20px;
}

.switch-btn {
  background: transparent;
  border: 1px solid white;
  color: white;
  padding: 12px 45px;
  border-radius: 25px;
  font-weight: bold;
  cursor: pointer;
  transition: 0.3s;
}

.switch-btn:hover {
  background: white;
  color: #41B883;
}

@media (max-width: 768px) {
  .login-card {
    display: none;
  }
}
</style>
