# 注册表单组件
<template>
  <form @submit.prevent="handleSubmit">
    <input 
      v-for="field in fields" 
      :key="field.name"
      :type="field.type"
      :placeholder="field.placeholder"
      v-model="formData[field.name]"
    >
    <button class="submit-btn">SIGN UP</button>
  </form>
</template>

<script setup>
import { reactive } from 'vue'

const fields = [
  { name: 'name', type: 'text', placeholder: 'Name' },
  { name: 'email', type: 'email', placeholder: 'Email' },
  { name: 'password', type: 'password', placeholder: 'Password' }
]

const formData = reactive({
  name: '',
  email: '',
  password: ''
})

const handleSubmit = () => {
  console.log('Form submitted:', formData)
}
</script>

<style scoped>
form {
  display: flex;
  flex-direction: column;
  gap: 10px;
  width: 100%;
  max-width: 300px;
}

input {
  padding: 12px 15px;
  border: 1px solid #ddd;
  border-radius: 5px;
  outline: none;
}

.submit-btn {
  background: #41B883;
  color: white;
  border: none;
  padding: 12px 45px;
  border-radius: 25px;
  font-weight: bold;
  cursor: pointer;
  transition: 0.3s;
}

.submit-btn:hover {
  background: #3aa876;
}
</style>
