# 注册卡片组件
<template>
  <div class="card signup-card">
    <h2>Create Account</h2>
    <SocialButtons />
    <p>or use your email for registration:</p>
    <SignupForm />
    <button class="mobile-switch" @click="$emit('switch-view')">
      Already have an account? Sign In
    </button>
  </div>
</template>

<script setup>
import SocialButtons from './SocialButtons.vue'
import SignupForm from './SignupForm.vue'

defineEmits(['switch-view'])
</script>

<style scoped>
.card {
  position: absolute;
  top: 0;
  height: 100%;
  transition: all 0.6s ease-in-out;
  padding: 50px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
}

.signup-card {
  right: 0;
  width: 50%;
  background: white;
  transform: translateX(0);
}

.auth-container.show-signup .signup-card {
  transform: translateX(-100%);
}

.mobile-switch {
  display: none;
  background: none;
  border: none;
  color: #666;
  margin-top: 15px;
  cursor: pointer;
}

@media (max-width: 768px) {
  .card {
    position: relative;
    width: 100%;
    padding: 30px;
  }

  .signup-card {
    transform: none;
  }

  .auth-container.show-signup .signup-card {
    transform: none;
  }

  .mobile-switch {
    display: block;
  }
}
</style>
