// `src/main.js`
import { createApp } from 'vue';
import App from './App.vue';
import './assets/styles.css';
import router from './router';
import store from './store';
import axios from './plugins/axios'; // 引入 Axios

// 挂载 Axios 到 Vue 的实例原型
createApp(App)
  .use(store) // 挂载 Vuex
  .use(router) // 挂载路由

  .mount('#app');
